import React, { Component } from "react"
import axios from "axios"

const {Provider, Consumer} = React.createContext()

class Context extends Component{
state = {
    something: ""
}

componentDidMount

render(){
    return(
        <Provider value={{something: this.state.something}}>
            {this.props.children}
        </Provider>
    )
}
}

export {Context, Consumer}
