requestMovie = () => {

    const randomPage = Math.floor(Math.random() * 2)
    console.log(randomPage)

    const random = Math.floor(Math.random() * 20)
    console.log(random)

    axios.get(`https://api.themoviedb.org/3/movie/top_rated?api_key=d246833b98ed7223e0d531d6ef59d00f&language=en-US&page=${randomPage}`)
    // (`https://api.themoviedb.org/3/movie/top_rated?api_key=d246833b98ed7223e0d531d6ef59d00f&language=en-US&page=1`)
    .then(res => {
        // console.log(res.data.results[random])
        // console.log(res.data.results[random].title)
        // console.log(res.data.results[random].overview)
        // console.log(res.data.results[random].backdrop_path)
        // console.log(res.data.results[random].id)
        this.setState({
            title: res.data.results[random].title,
            overview: res.data.results[random].overview,
            release_date: res.data.results[random].release_date,
            backdrop_path: `https://www.themoviedb.org/t/p/w533_and_h300_bestv2${res.data.results[random].backdrop_path}`,
            id: res.data.results[random].id
        })
    })
}

componentDidMount = () => {
    console.log("did mount")
    this.requestMovie()
}

handleChange = (e) => {
    e.preventDefault()
    console.log("HandleChange")
}

handleClickChooseNewMovie = () => {
    console.log("Choose New Movie")
    this.requestMovie()
    }

handleClickChoose = () => {
    console.log("Choose Movie")
    this.requestMovie()
        }

handleClickReview = () => {
console.log("Review")
}

handleClickFormSubmit = () => {
    console.log("Form Submitted")
    }

render(){
    return(
        <Provider value={{
            title: this.state.title, overview: this.state.overview, release_date: this.state.release_date, backdrop_path: this.state.backdrop_path, id: this.state.id, handleChange: this.handleChange, handleClickReview: this.handleClickReview, handleClickChoose: this.handleClickChoose, handleClickChooseNewMovie: this.handleClickChooseNewMovie, handleClickFormSubmit: this.handleClickFormSubmit}}>
            {this.props.children}
        </Provider>
    )
}
}

export {Context, Consumer}
