            </div>
            <div>
              <label>
                <input
                  type="radio"
                  value="4"
                  name="starRating"
                  checked={this.state.selectedOption === "4"}
                  // onClick={context.onValueChange}
                />
                ⭐⭐⭐⭐
              </label>
            </div>
            <div>
              <label>
                <input
                  type="radio"
                  value="5"
                  name="starRating"
                  checked={this.state.selectedOption === "5"}
                  // onClick={context.onValueChange}
                />
                ⭐⭐⭐⭐⭐
              </label>
            </div>
            <div>
              <label>
                <input placeholder="Write a review" type="textArea" value={this.state.handleChange} />
              </label>
            </div>
          </div>
          <button onClick={this.postRequest} >Submit Movie Rating</button>
        </form>
      </div>
                )
            }}
        </Consumer>




