import React from "react";
import { Consumer } from "./Context";

export default function Form() {
  return (
    <Consumer>
      {(context) => {
        return (
          <div>
            <form className="formContainer">
              <div className="radioButtons">
                <div>
                  <label>
                    <input
                      type="radio"
                      value="1"
                      name="starRating"
                      checked={context.selectedOption === "1"}
                      // onClick={context.onValueChange}
                    />
                    ⭐
                  </label>
                </div>
                <div>
                  <label>
                    <input
                      type="radio"
                      value="2"
                      name="starRating"
                      checked={context.selectedOption === "2"}
                      // onClick={context.onValueChange}
                    />
                    ⭐⭐
                  </label>
                </div>
                <div>
                  <label>
                    <input
                      type="radio"
                      value="3"
                      name="starRating"
                      checked={context.selectedOption === "3"}
                      // onClick={context.onValueChange}
                    />
                    ⭐⭐⭐
                  </label>
                </div>
                <div>
                  <label>
                    <input
                      type="radio"
                      value="4"
                      name="starRating"
                      checked={context.selectedOption === "4"}
                      // onClick={context.onValueChange}
                    />
                    ⭐⭐⭐⭐
                  </label>
                </div>
                <div>
                  <label>
                    <input
                      type="radio"
                      value="5"
                      name="starRating"
                      checked={context.selectedOption === "5"}
                      // onClick={context.onValueChange}
                    />
                    ⭐⭐⭐⭐⭐
                  </label>
                </div>
                <div>
                  <label>
                    <input type="textArea" value={context.onChange} />
                  </label>
                </div>
              </div>
              <button onSubmit={context.formSubmit}>Submit Movie Rating</button>
            </form>
            {context.textbox}
          </div>
        );
      }}
    </Consumer>
  );
}
