import React from "react";
import { Consumer} from "./Context";

export default function Movie(props) {
  return (
    <Consumer>
      {(context) => {

        return (
          <div className="movieContainer">
            <div className="movieTitle">
                {context.title}
            </div>
            <br></br>
            <div className="movieImage">
                <img src={context.backdrop_path} alt={context.title} width="300" height="200"></img>
            </div>
            <div className="movieOverview">
                What this move is about: {context.overview}
            </div>
            <div className="movieReleaseDate">
                This move was released in: {context.release_date}
            </div>
            <Link to="/Reviews"> ⭐⭐⭐
                <button onClick={context.handleClickReview}>Reviews & Ratings</button>

             </Link>
          </div>
        );
      }}
    </Consumer>
  );
}
