import React from "react";
import { Consumer } from "./Context";

export default function Form() {
  return (
    <Consumer>
      {(context) => {
        return (
          <div>
            <form className="formContainer">
              <h1 className="formHeader">Please write your own review on {context.title}</h1>
              <div className="formContent"><h3>Would you cecomend this movie</h3></div>
            </form>
          </div>
        );
      }}
    </Consumer>
  );
}
