import React, { Component } from "react"
import axios from "axios"

const {Provider, Consumer} = React.createContext()

class Context extends Component{
state = {
    movieTitle: "",
    cast: "",
    description: "",
    imageUrl: "",
    release: "",
    form: []

}

randomPage = () => {
    Math.floor(Math.random() * Math.floor())
    // console.log(randomPage)
}

requestMovie = () => {

    axios.get(`https://api.themoviedb.org/3/movie/top_rated?api_key=d246833b98ed7223e0d531d6ef59d00f&language=en-US&page=`)
    .then(res => {
        console.log(res)
        // this.setState({})
    })
}

componentDidMount = () => {
    console.log("did mount")
    this.requestMovie()
}

handleChange = (e) => {
    e.preventDefault()
    console.log("HandleChange")
}

handleClickChoose = () => {
    console.log("Choose Movie")
    }

handleClickReview = () => {
console.log("Review")
}

handleClickFormSubmit = () => {
    console.log("Form Submitted")
    }

render(){
    return(
        <Provider value={{movie: this.state.movie, imageUrl: this.state.imageUrl, handleChange: this.handleChange, handleClickReview: this.handleClickReview, handleClickChoose: this.handleClickChoose, handleClickFormSubmit: this.handleClickFormSubmit}}>
            {this.props.children}
        </Provider>
    )
}
}

export {Context, Consumer}
