import React from "react";
import {Consumer} from "./Context"
import { Link } from "react-router-dom";

export default function Home(props){
    return(
        <Consumer>
            {(context) => {
                return(
                    <div className="containerHome">
                        <h1 className="titleHome">
                            {context.title}
                        </h1>
                        <div className="imageHome">
                        <Link to="/Movie">
                            <img src={context.backdrop_path} alt= "Movie Poster" width="300" height="200"></img>
                            <p>Click to See more about {context.title}}</p>
                        </Link>
                            <p>
                                Click Image to Select Movie
                            </p>
                        </div>
                        <button onClick={context.handleClickChooseNewMovie}>Shuffle Movie</button>
                    </div>
                )
            }}
        </Consumer>
    )
}
