import React from "react";
import { Consumer } from "./Context";

export default function Form() {
  return (
    <Consumer>
      {(context) => {
        return (
          <div>
            <form
              className="formContainer"
              onChange={context.handleChange}
              onSubmit={context.formSubmit}
            >
              <div>
                <lable>
                  <input type="radio" value="1" checked={context.selecetedOption === "1"}>
                    ⭐
                  </input>
                </lable>
              </div>
              <div>
                <lable>
                  <input type="radio" value="2" checked={context.selecetedOption === "2"}>
                    ⭐⭐{" "}
                  </input>
                </lable>
              </div>
              <div>
                <lable>
                  <input type="radio" value="3" checked={context.selecetedOption === "3"}>
                    ⭐⭐⭐{" "}
                  </input>
                </lable>
              </div>
              <div>
                <lable>
                  <input type="radio" value="4">
                    ⭐⭐⭐⭐
                  </input>
                </lable>
              </div>
              <div>
                <lable>
                  <input type="radio" value="5">
                    ⭐⭐⭐⭐⭐
                  </input>
                </lable>
              </div>
              <input type="submit" />
            </form>
          </div>
        );
      }}
    </Consumer>
  );
}
