import React from "react";
import {Consumer} from "./Context"

export default function Home(props){
    return(
        <Consumer>
            {(context) => {
                return(
                    <div className="containerHome">
                        <h1 className="titleHome">
                            {context.title}
                        </h1>
                        <div className="imageHome">
                        <img src={context.backdrop_path} alt= "Movie Poster" width="300" height="200"></img>
                            <p>
                                Click Image to Select Movie
                            </p>
                        </div>
                        <button onClick={context.handleClickChoose} className="buttonHome">Shuffle Movie</button>
                        <button onClick={context.handleClickChooseNewMovie} > Choose Movie</button>
                    </div>
                )
            }}
        </Consumer>
    )
}
