import React, { Component } from "react"
import axios from "axios"

const {Provider, Consumer} = React.createContext()

class Context extends Component{
state = {
    destination: "",
    imageUrl: "",


}

requestLocationData = () => {
    axios.get(`http://www.omdbapi.com/?apikey=4041277b&`)
    .then(res => {
        console.log(res)
        // this.setState({})
    })
}

componentDidMount = () => {
    console.log("did mount")
    this.requestLocationData()
}

render(){
    return(
        <Provider value={{destination: this.state.destination, imageUrl: this.state.imageUrl}}>
            {this.props.children}
        </Provider>
    )
}
}

export {Context, Consumer}
