import React, { Component } from "react"
import axios from "axios"

const {Provider, Consumer} = React.createContext()

class Context extends Component{
state = {
    movieTitle: "",
    cast: "",
    description: "",
    imageUrl: "",
    release: ""

}


requestMovie = () => {
    axios.get(`https://imdb8.p.rapidapi.com/title/auto-complete`)
    // (https://api.vschool.io/mackyandow/todo
    .then(res => {
        console.log(res)
        // this.setState({})
    })
}

componentDidMount = () => {
    console.log("did mount")
    this.requestMovie()
}

handleClickChoose = () => {
    console.log("Choose Movie")
    }

handleClickReview = () => {
console.log("Review")
}

handleClickFormSubmit = () => {
    console.log("Form Submitted")
    }


render(){
    return(
        <Provider value={{movie: this.state.movie, imageUrl: this.state.imageUrl, handleClickReview: this.handleClickReview, handleClickChoose: this.handleClickChoose, handleClickFormSubmit: this.handleClickFormSubmit}}>
            {this.props.children}
        </Provider>
    )
}
}

export {Context, Consumer}
