import React from "react";
import Form from "./Form";
import Reviews from "./Reviews";
import { Consumer } from "./Context";

export default function Review(props) {
  return (
    <Consumer>
      {(context) => {
        return (
          <div className="reviewContainer">
            <div className="reviewHeader">
              <br></br>
              {/* <h1>Reviews on {context.title}</h1> */}
              <div
                className="revImg"
                style={{ backgroundImage: `"${context.backdrop_path}"` }}
              >
                <img src={context.backdrop_path} alt={context.title}></img>
              </div>
            </div>
            <h1>What did you think about {context.title}</h1>
            <div className="formRate">
              <Form />
            </div>
            <div className="writtenReviews">
              <Reviews />
            </div>
          </div>
        );
      }}
    </Consumer>
  );
}
