        <Provider value={{movie: this.state.movie, imageUrl: this.state.imageUrl, handleChange: this.handleChange, handleClickReview: this.handleClickReview, handleClickChoose: this.handleClickChoose, handleClickFormSubmit: this.handleClickFormSubmit}}>
            {this.props.children}
        </Provider>
    )
}
}

export {Context, Consumer}
