import React from "react";
import axios from "axios"
// import { Consumer } from "./Context";

// import React, { Component } from "react";

export default class Form extends React.Component {
  state = {
    reviews: "",
    selectedOption: "",
    textArea: ""
  };

  submitReview = (e) => {
    e.preventDefault()
    console.log(this.state.reviews)
    this.setState(prevState => {
      const review = {
        selectedOption: prevState.selectedOption,
        textArea: prevState.textArea
      }
      return(
        {
          reviews: [...prevState.reviews, review],
          review: ""
        }
        )
      })
      console.log(this.state.reviews)
  }

  requestData = () => {
    axios.get(`https://api.vschool.io/mackyandowL4/thing`)
    .then(res => {
        this.setState({ureviews: res.data})
    })
}

    postRequest = () => {
      const newObj = {
         reviews:this.state.reviews
      }

      axios.post(`https://api.vschool.io/mackyandow/thing`, newObj)
      .then(res => {
          this.requestData()
      })

      this.requestData()
  }

  render() {
    return (
      <div>
        <form className="formContainer">
          <div className="radioButtons">
            <div>
              <label>
                <input
                  type="radio"
                  value="1"
                  name="starRating"
                  checked={this.state.selectedOption === "1"}
                  // onClick={context.onValueChange}
                />
                ⭐
              </label>
            </div>
            <div>
              <label>
                <input
                  type="radio"
                  value="2"
                  name="starRating"
                  checked={this.state.selectedOption === "2"}
                  // onClick={context.onValueChange}
                />
                ⭐⭐
              </label>
            </div>
            <div>
              <label>
                <input
                  type="radio"
                  value="3"
                  name="starRating"
                  checked={this.state.selectedOption === "3"}
                  // onClick={context.onValueChange}
                />
                ⭐⭐⭐
              </label>
            </div>
            <div>
              <label>
                <input
                  type="radio"
                  value="4"
                  name="starRating"
                  checked={this.state.selectedOption === "4"}
                  // onClick={context.onValueChange}
                />
                ⭐⭐⭐⭐
              </label>
            </div>
            <div>
              <label>
                <input
                  type="radio"
                  value="5"
                  name="starRating"
                  checked={this.state.selectedOption === "5"}
                  // onClick={context.onValueChange}
                />
                ⭐⭐⭐⭐⭐
              </label>
            </div>
            <div>
              <label>
                <input placeholder="Write a review" type="textArea" value={this.state.handleChange} />
              </label>
            </div>
          </div>
          <button onClick={this.postRequest} >Submit Movie Rating</button>
        </form>
      </div>
    );
  }
}

