import React, { Component } from "react"
import { Link } from "react-router-dom";
import axios from "axios"

const {Provider, Consumer} = React.createContext()

class Context extends Component{
state = {
    title: "",
    overview: "",
    release_date: "",
    backdrop_path: "",
    id: ""

}

requestMovie = () => {

    // const randomPage = Math.floor(Math.random() * 2)
    // console.log(randomPage)

    const random = Math.floor(Math.random() * 20)
    console.log(random)

    axios.get(`https://api.themoviedb.org/3/movie/top_rated?api_key=d246833b98ed7223e0d531d6ef59d00f&language=en-US&page=1`)
    // ${randomPage}
    // (`https://api.themoviedb.org/3/movie/top_rated?api_key=d246833b98ed7223e0d531d6ef59d00f&language=en-US&page=1`)
    .then(res => {
        // console.log(res.data.results[random])
        // console.log(res.data.results[random].title)
        // console.log(res.data.results[random].overview)
        // console.log(res.data.results[random].backdrop_path)
        // console.log(res.data.results[random].id)
        this.setState({
            title: res.data.results[random].title,
            overview: res.data.results[random].overview,
            release_date: res.data.results[random].release_date,
            backdrop_path: `https://www.themoviedb.org/t/p/w533_and_h300_bestv2${res.data.results[random].backdrop_path}`,
            id: res.data.results[random].id
        })
    })
}

componentDidMount = () => {
    console.log("did mount")
    this.requestMovie()
}

onValueChange = (event) => {
    this.setState({
      selectedOption: event.target.value
    });
  }

handleClickChooseNewMovie = () => {
    console.log("Choose New Movie")
    this.requestMovie()
    }

handleClickReview = () => {
    <Link to="/Reviews"> ⭐⭐⭐ </Link>
console.log("Review")
}

formSubmit = (e) => {
    e.preventDefault()
}

render(){
    return(
        <Provider value={{
            title: this.state.title, 
            overview: this.state.overview, 
            release_date: this.state.release_date, 
            backdrop_path: this.state.backdrop_path, 
            id: this.state.id, 
            value: this.state.value,
            text: this.state.text,
            review: this.state.review,
            reviews: this.state.reviews,
            handleChange: this.handleChange, 
            selectedOption: this.selectedOption,
            onValueChange: this.onValueChange,
            handleClickReview: this.handleClickReview, 
            handleClickChooseNewMovie: this.handleClickChooseNewMovie, 
            formSubmit: this.formSubmit}}>
            {this.props.children}
        </Provider>
    )
}
}

export {Context, Consumer}
